close all
figure()
axis equal
axis([0 105 0 16])
grid on
finish = 0;
letter = zeros(17,106);
while ~finish
  axis equal
  axis([0 105 0 16])
  [x,y] = ginput(1);
  coordx = floor(x);
  coordy = floor(y);
  if coordx > 105 || coordy > 17
    finish = 1;
  else
    rectangle('Position',[coordx,coordy,1,1],'Facecolor','k','EdgeColor','none')
    try 
      if letter(coordy+1,coordx+1) == 1
	rectangle('Position',[coordx,coordy,1,1],'Facecolor',[1 1 1],'EdgeColor','none')
	letter(coordy+1,coordx+1) = 0;
      else
	letter(coordy+1,coordx+1) = 1;
      end
    catch
      disp('Null')
    end
  end
end

binary_number = unwrapmatrix(letter');
str = num2str(binary_number);
str(str == ' ') = []

